// Get all items (these are the captions)
const allItems = $input.all();

// Get data directly from the trigger node
const triggerData = $('When Executed by Another Workflow').first().json;
const selectedPlatforms = triggerData.body?.data?.Social || triggerData.data?.Social || triggerData.Social;
const kiHinweis = triggerData.body?.data?.kiHinweis || triggerData.data?.kiHinweis || triggerData.kiHinweis || '';

if (!selectedPlatforms || !Array.isArray(selectedPlatforms)) {
  throw new Error('Could not find Social platforms. Available keys: ' + JSON.stringify(Object.keys(triggerData)));
}

// Create result object
const result = {};

// Assign each caption directly to its platform
for (let i = 0; i < selectedPlatforms.length; i++) {
  const platform = selectedPlatforms[i];

  // Get caption from the corresponding item
  const item = allItems[i];
  let caption = '';
  if (item) {
    caption = item.json?.message?.content || item.json?.caption || item.json?.output || '';
  }

  // Append KI-Hinweis if provided (with two line breaks)
  if (kiHinweis && caption) {
    caption = caption + '\n\n' + kiHinweis;
  }

  // Map platform name to lowercase key
  let key;
  if (platform === 'Instagram') key = 'instagram';
  else if (platform === 'Linkedin') key = 'linkedin';
  else if (platform === 'Facebook') key = 'facebook';
  else if (platform === 'Pressemitteilung') key = 'pressemitteilung';
  else if (platform === 'InterneEmail') key = 'interneemail';
  else key = platform.toLowerCase();

  result[key] = { caption: caption };
}

return result;
