// Get input data from workflow trigger
const triggerData = $('When Executed by Another Workflow').first().json;

// Get the generated image binary data
const imageItems = $input.all();
let imageUrl = '';
let imageBinary = null;

// Check if we have generated image data
if (imageItems && imageItems.length > 0 && imageItems[0].binary) {
  const binaryKeys = Object.keys(imageItems[0].binary);
  if (binaryKeys.length > 0) {
    imageBinary = imageItems[0].binary[binaryKeys[0]];
    // Convert binary to base64 data URL for frontend display
    if (imageBinary.data) {
      const mimeType = imageBinary.mimeType || 'image/png';
      imageUrl = `data:${mimeType};base64,${imageBinary.data}`;
    }
  }
}

return [{
  json: {
    instagramCaption: triggerData.instagramCaption || '',
    linkedinCaption: triggerData.linkedinCaption || '',
    facebookCaption: triggerData.facebookCaption || '',
    pressemitteilungCaption: triggerData.pressemitteilungCaption || '',
    interneEmailCaption: triggerData.interneEmailCaption || '',
    imageUrl: imageUrl,
    imageGenerated: true
  },
  binary: imageBinary ? { 'generated_image': imageBinary } : {}
}];

// Pass through the original data without image
const triggerData = $('When Executed by Another Workflow').first().json;

return [{
  json: {
    instagramCaption: triggerData.instagramCaption || '',
    linkedinCaption: triggerData.linkedinCaption || '',
    facebookCaption: triggerData.facebookCaption || '',
    pressemitteilungCaption: triggerData.pressemitteilungCaption || '',
    interneEmailCaption: triggerData.interneEmailCaption || '',
    imageUrl: '',
    imageGenerated: false
  }
}];
